mapper.select:
SELECT A.* FROM ( SELECT ROWNUM as bookNo , ( SELECT scodes.scode_nm FROM scodes WHERE lcode = 'BF' AND scode = b.book_fl_cd ) as book_fl_cd , b.book_id , b.ins_dt , b.book_cover , b.book_cover_path , b.book_nm , b.book_publ_co , b.book_writer , b.book_cnfm_yn , b.book_useyn , b.book_hit , ( select count(1) from book_attach at where at.book_id = b.book_id) as fileCnt , b.member_id , ( SELECT member_nm FROM member m WHERE m.member_id = b.member_id ) member_nm , nvl( ( select scode_nm from scodes s where s.lcode = 'BC' and s.scode = c.bcnfm_st_cd ), '미신청') as cnfm_St_Nm FROM BOOK B , BOOK_CONFIRM C WHERE b.book_id = c.book_id(+) and b.bcnfm_id = c.bcnfm_id (+) and b.member_Id = #{memberId}
and c.bcnfm_st_cd = #{srchCnfmCd}
and b.book_cnfm_yn = 'N' and b.bcnfm_id is null
and b.book_publ_co like '%' || #{bookPublCo} || '%'
and b.book_writer like '%' || #{bookWriter} || '%'
and b.book_nm like '%' || #{bookNm} || '%'
and b.book_fl_cd = #{bookFlCd}
and b.ins_dt = #{insDt}
and b.book_publ_dt between #{bookPublSDt} and #{bookPublEDt}
and b.book_publ_dt between #{bookPublSDt} and #{bookPublEDt}
and b.ctgy_id = #{ctgyId}
and b.ctgy_id in ( select ctgy_id from category where ctgy_Gr_id = #{ctgyGrId} )
ORDER BY rownum DESC ) A WHERE bookNo >= #{start} and bookNo <= #{end}

mapper.update:
UPDATE book 
   SET CTGY_ID = #{ctgyId}
   	 , BOOK_NM = #{bookNm}
   	 , BOOK_FL_CD = #{bookFlCd}
     , BOOK_PUBL_CO = #{bookPublCo}
     , BOOK_WRITER = #{bookWriter}
     , BOOK_PUBL_DT = #{bookPublDt}
     , BOOK_INTRO = #{bookIntro}
     , BOOK_CONTENT = #{bookContent}
     , BOOK_ISBN = #{bookIsbn}
     , BOOK_WRITER_INTRO = #{bookWriterIntro}
     , BOOK_DESC = #{bookDesc}
     , BOOK_COVER = #{bookCover}
     , BOOK_COVER_PATH = #{bookCoverPath}
     , UDT_DT = SYSDATE
 WHERE BOOK_ID = #{bookId}

UPDATE book 
   SET BOOK_COVER = #{bookCover}
     , BOOK_COVER_PATH = #{bookCoverPath}
     , UDT_DT = SYSDATE
 WHERE BOOK_ID = #{bookId}

UPDATE BOOK 
   SET book_useyn = 'N'
 WHERE book_id = #{bookId}

mapper.select:
SELECT COUNT(*) FROM ( SELECT ROWNUM as bookNo , ( SELECT scodes.scode_nm FROM scodes WHERE lcode = 'BF' AND scode = b.book_fl_cd ) as book_fl_cd , b.book_id , b.ins_dt , b.book_cover , b.book_cover_path , b.book_nm , b.book_publ_co , b.book_writer , b.book_cnfm_yn , b.book_useyn , b.book_hit , b.member_id , ( SELECT member_nm FROM member m WHERE m.member_id = b.member_id ) member_nm , nvl( ( select scode_nm from scodes s where s.lcode = 'BC' and s.scode = c.bcnfm_st_cd ), '미신청') as cnfm_St_Nm FROM BOOK B , BOOK_CONFIRM C WHERE b.book_id = c.book_id(+) and b.bcnfm_id = c.bcnfm_id (+) and b.member_Id = #{memberId}
and c.bcnfm_st_cd = #{srchCnfmCd}
and b.book_cnfm_yn = 'N' and b.bcnfm_id is null
and b.book_publ_co like '%' || #{bookPublCo} || '%'
and b.book_writer like '%' || #{bookWriter} || '%'
and b.book_nm like '%' || #{bookNm} || '%'
and b.book_fl_cd = #{bookFlCd}
and b.ins_dt = #{insDt}
and b.book_publ_dt between #{bookPublSDt} and #{bookPublEDt}
and b.book_publ_dt between #{bookPublSDt} and #{bookPublEDt}
and b.ctgy_id = #{ctgyId}
and b.ctgy_id in ( select ctgy_id from category where ctgy_Gr_id = #{ctgyGrId} )
) A

SELECT A.* FROM ( SELECT ROWNUM as bookNo , ( SELECT scodes.scode_nm FROM scodes WHERE lcode = 'BF' AND scode = b.book_fl_cd ) as book_fl_cd , b.book_id , b.ins_dt , b.book_cover , b.book_cover_path , b.book_nm , b.book_isbn , b.book_publ_dt , b.book_publ_co , b.book_writer , b.book_likeit , b.book_hit , ( select ctgy_nm from category where ctgy_id in ( select ctgy_gr_id from category where ctgy_id = b.ctgy_id ) ) as ctgy_gr_nm , ( select ctgy_nm from category where ctgy_id = b.ctgy_id ) ctgy_nm FROM BOOK B
and b.book_publ_co like '%' || #{bookPublCo} || '%'
and b.book_writer like '%' || #{bookWriter} || '%'
and b.book_nm like '%' || #{bookNm} || '%'
and b.book_fl_cd = #{bookFlCd}
and b.ins_dt = #{insDt}
and b.book_publ_dt between #{bookPublSDt} and #{bookPublEDt}
and b.book_publ_dt between #{bookPublSDt} and #{bookPublEDt}
and b.ctgy_id = #{ctgyId}
and b.ctgy_id in ( select ctgy_id from category where ctgy_Gr_id = #{ctgyGrId} )
ORDER BY rownum DESC ) A WHERE bookNo >= #{start} and bookNo <= #{end}

SELECT count(*) FROM ( SELECT ROWNUM as bookNo , ( SELECT scodes.scode_nm FROM scodes WHERE lcode = 'BF' AND scode = b.book_fl_cd ) as book_fl_cd , b.book_id , b.ins_dt , b.book_cover , b.book_cover_path , b.book_nm , b.book_isbn , b.book_publ_dt , b.book_publ_co , b.book_writer , b.book_likeit , b.book_hit , ( select ctgy_nm from category where ctgy_id in ( select ctgy_gr_id from category where ctgy_id = b.ctgy_id ) ) as ctgy_gr_nm , ( select ctgy_nm from category where ctgy_id = b.ctgy_id ) ctgy_id_nm FROM BOOK B
and b.book_publ_co like '%' || #{bookPublCo} || '%'
and b.book_writer like '%' || #{bookWriter} || '%'
and b.book_nm like '%' || #{bookNm} || '%'
and b.book_fl_cd = #{bookFlCd}
and b.ins_dt = #{insDt}
and b.book_publ_dt between #{bookPublSDt} and #{bookPublEDt}
and b.book_publ_dt between #{bookPublSDt} and #{bookPublEDt}
and b.ctgy_id = #{ctgyId}
and b.ctgy_id in ( select ctgy_id from category where ctgy_Gr_id = #{ctgyGrId} )
ORDER BY rownum DESC ) A

mapper.update:
UPDATE BOOK 
   SET book_hit = book_hit + 1 
 WHERE book_id = #{bookId}

