mapper.select:
   SELECT max(breply_id) as breplyId   
	 FROM BOOK_REPLY
	WHERE BOOK_ID = #{bookId}

mapper.update:
UPDATE BOOK_REPLY
   SET breply_contents = #{breplyContents}
     , udt_dt = SYSDATE
 WHERE book_id = #{bookId}
   AND breply_id = #{breplyId}

mapper.delete:
DELETE FROM BOOK_REPLY
 WHERE book_id = #{bookId}
   AND breply_id = #{breplyId}
