mapper.select:
SELECT * 
  FROM BUCKET_BOOK
 WHERE member_id = #{memberId} 
   AND book_id = #{bookId}

mapper.update:
UPDATE bucket_book
   SET bucket_done_dt = SYSDATE
     , bucket_st_cd = '02'
     , udt_dt = SYSDATE
 WHERE bucket_id = #{bucketId}
   AND book_id = #{bookId}
   AND MEMBER_ID = #{memberId}

mapper.delete:
delete bucket_book
 WHERE bucket_id = #{bucketId}
   AND book_id = #{bookId}
   AND MEMBER_ID = #{memberId}

mapper.update:
UPDATE bucket_book
   SET bucket_done_dt = null
     , bucket_st_cd = '01'
     , udt_dt = SYSDATE
 WHERE bucket_id = #{bucketId}
   AND book_id = #{bookId}
   AND MEMBER_ID = #{memberId}
