mapper.select:
SELECT *
  FROM BOOK_CART
 WHERE MEMBER_ID = #{memberId}  
   AND BOOK_ID = #{bookId}

mapper.insert:
INSERT INTO book_cart ( 
	CART_ID 		, BOOK_ID	, MEMBER_ID		, INS_DT	, UDT_DT 
) values (
	BC_SEQ.NEXTVAL	, #{bookId} , #{memberId}	, SYSDATE 	, SYSDATE 
)

mapper.delete:
		DELETE FROM BOOK_CART
  		 WHERE MEMBER_ID = #{memberId}
		   AND BOOK_ID = #{bookId}
