mapper.select:
select  * 
  from  member 
 where 	member_nm = #{memberNm} 
   and  member_tel = #{memberTel}

select  * 
  from  member 
 where 	member_id = #{memberId} 
   and  member_nm = #{memberNm}
   and  member_tel = #{memberTel}

mapper.update:
update  member
   set  member_pw = #{memberPw}
 where  member_id = #{memberId}
